mapper.select:
SELECT
  a.*,
  b.CRON_EXPRESSION,
  b.TIME_ZONE_ID
FROM QRTZ_TRIGGERS a
  LEFT JOIN QRTZ_CRON_TRIGGERS b ON (a.SCHED_NAME=b.SCHED_NAME AND a.TRIGGER_GROUP=b.TRIGGER_GROUP AND a.TRIGGER_NAME=b.TRIGGER_NAME)
WHERE b.SCHED_NAME IS NOT NULL
      AND a.SCHED_NAME = #{schedName}
      AND a.JOB_NAME = #{jobName}
      AND a.JOB_GROUP = #{jobGroup}

SELECT
  a.*,
  b.BLOB_DATA
FROM QRTZ_TRIGGERS a
  LEFT JOIN QRTZ_BLOB_TRIGGERS b ON (a.SCHED_NAME=b.SCHED_NAME AND a.TRIGGER_GROUP=b.TRIGGER_GROUP AND a.TRIGGER_NAME=b.TRIGGER_NAME)
WHERE b.SCHED_NAME IS NOT NULL
      AND a.SCHED_NAME = #{schedName}
      AND a.JOB_NAME = #{jobName}
      AND a.JOB_GROUP = #{jobGroup}
